mapper.select:
select a.MHID, a.NAME, a.TYPE, (SELECT d.`name` from oa_company_basic d WHERE a.type= d.ocbid) as typeName, a.MODEL, a.POWER, a.CHARGE, a.DAY_REPAIRMAN, a.NIGHT_REPAIRMAN, (SELECT c.NAME from oa_staff c where c.STAFF_ID = a.charge) as chargeName, (SELECT c.NAME from oa_staff c where c.STAFF_ID = a.Day_Repairman) as DayName, (SELECT c.NAME from oa_staff c where c.STAFF_ID = a.Night_Repairman) as NightName, a.BARCODE, a.BARCODEURL, a.COMPANY_ID, a.DELETED, a.BZ, a.CHANGE_RULE, CASE WHEN CHANGE_RULE = 0 THEN '支持' ELSE '不支持' END as CHANGERULEName from a LEFT JOIN oa_staff e ON e.STAFF_ID = a.charge where a.COMPANY_ID = #{pd.COMPANY_ID} and a.deleted = 0
and a.Name LIKE CONCAT(CONCAT('%', #{pd.Name}),'%')
and a.TYPE = #{pd.TYPE}
and e.Name LIKE CONCAT(CONCAT('%', #{pd.Repairman}),'%')
and a.CHANGE_RULE = #{pd.CHANGE_RULE}

