SET deleted= 1 where MHID in
#{item}

mapper.select:
SELECT a.name,b.name as typeName,a.barcode,a.mhid, (SELECT d.USERNAME from oa_staff c LEFT JOIN sys_user d ON c.USER_ID = d.USER_ID where c.STAFF_ID = a.charge) as chargeName, (SELECT d.USERNAME from oa_staff c LEFT JOIN sys_user d ON c.USER_ID = d.USER_ID where c.STAFF_ID = a.Day_Repairman) as Day_Repairman, (SELECT d.USERNAME from oa_staff c LEFT JOIN sys_user d ON c.USER_ID = d.USER_ID where c.STAFF_ID = a.Night_Repairman) as Night_Repairman, (SELECT c.NAME from oa_staff c where c.STAFF_ID = a.charge) as chargeN, (SELECT c.NAME from oa_staff c where c.STAFF_ID = a.Day_Repairman) as Day_R, (SELECT c.NAME from oa_staff c where c.STAFF_ID = a.Night_Repairman) as Night_R from OA_MACHINE a LEFT JOIN oa_company_basic b ON a.type = b.ocbid where a.company_id = #{company_id}
and a.barcode = #{machineCode}
and a.name = #{name}
