mapper.select:
select a.srid, a.mhid, a.staff_Id, a.staff_name, a.car_id, a.car_code, CASE WHEN a.scan_type = 1 THEN '巡视扫描' WHEN a.scan_type = 2 THEN '维修扫描' WHEN a.scan_type = 3 THEN '保养扫描' WHEN a.scan_type = 4 THEN '改规格扫描' END as scan_type, CASE WHEN a.operation_type = '0' THEN '开始操作' WHEN a.operation_type = '1' THEN '结束操作' ELSE '' END as operation_type, a.repair_position, a.target_rule, a.scan_date, a.cycle_type, b.name as mname, b.barcode as mcode, c.name as carName, c.barcode as carCode from a LEFT JOIN oa_machine b ON a.mhid = b.mhid LEFT JOIN oa_car c ON c.car_id = a.car_id where a.COMPANY_ID =#{pd.COMPANY_ID}
and a.scan_type IN (${pd.scan_type})
and b.name LIKE CONCAT(CONCAT('%', #{pd.machineName}),'%')
and a.scan_type =#{pd.selectScanType}
and c.NAME LIKE CONCAT(CONCAT('%', #{pd.carName}),'%')
and a.staff_name LIKE CONCAT(CONCAT('%', #{pd.staffName}),'%')
and a.scan_date >=#{pd.lastStart}
and a.scan_date <= CONCAT(#{pd.lastEnd},' 23:59:59')
and a.operation_type = #{pd.changeType}
ORDER BY scan_date DESC

SELECT a.name as machineName,a.barcode as machineCode, (SELECT b.`name` from oa_company_basic b where a.type = b.ocbid) as typeName, (SELECT count(1) from oa_scan_record c where c.mhid = a.mhid and c.scan_date>=#{pd.lastStart} and c.scan_date<=CONCAT(#{pd.lastEnd},' 23:59:59') and c.scan_type='2') as xsCount, g.cycleInfo, (SELECT count(1)/3 FROM sys_fhsms h where h.mhid = a.mhid and h.SEND_TIME>=#{pd.lastStart} and h.SEND_TIME<=CONCAT(#{pd.lastEnd},' 23:59:59') and h.SMS_TYPE='2') as wxtzCount, (SELECT count(1) from oa_scan_record i where i.mhid = a.mhid and i.operation_type='0' and i.scan_type='2' and i.scan_date >=#{pd.lastStart} and i.scan_date<=CONCAT(#{pd.lastEnd},' 23:59:59')) as wxksCount, (SELECT count(1) from oa_scan_record i where i.mhid = a.mhid and i.operation_type='1' and i.scan_type='2' and i.scan_date >=#{pd.lastStart} and i.scan_date<=CONCAT(#{pd.lastEnd},' 23:59:59')) as wxjsCount, ifnull((SELECT avg(TIMESTAMPDIFF(MINUTE,j.notice_date,j.start_date))from oa_machine_record j WHERE j.mhid = a.mhid and j.scan_type='2' and j.start_date>=#{pd.lastStart} and j.start_date<=CONCAT(#{pd.lastEnd},' 23:59:59') GROUP BY j.mhid),0) as wxavgtz, ifnull((SELECT avg(TIMESTAMPDIFF(MINUTE,j.start_date,j.end_date))from oa_machine_record j WHERE j.mhid = a.mhid and j.scan_type='2' and j.start_date>=#{pd.lastStart} and j.start_date<=CONCAT(#{pd.lastEnd},' 23:59:59') GROUP BY j.mhid),0) as wxavgwx, ifnull((SELECT sum(TIMESTAMPDIFF(MINUTE,j.start_date,j.end_date))from oa_machine_record j WHERE j.mhid = a.mhid and j.scan_type='2' and j.start_date>=#{pd.lastStart} and j.start_date<CONCAT(#{pd.lastEnd},' 23:59:59') GROUP BY j.mhid),0) as wxsum,
(SELECT count(1)/3 FROM sys_fhsms h where h.mhid = a.mhid and h.SEND_TIME>#{pd.lastStart} and h.SEND_TIME<=CONCAT(#{pd.lastEnd},' 23:59:59') and h.SMS_TYPE='4') as gggtzCount, (SELECT count(1) from oa_scan_record i where i.mhid = a.mhid and i.operation_type='0' and i.scan_type='4' and i.scan_date >=#{pd.lastStart} and i.scan_date<=CONCAT(#{pd.lastEnd},' 23:59:59')) as gggksCount, (SELECT count(1) from oa_scan_record i where i.mhid = a.mhid and i.operation_type='1' and i.scan_type='4' and i.scan_date >=#{pd.lastStart} and i.scan_date<=CONCAT(#{pd.lastEnd},' 23:59:59')) as gggjsCount, ifnull((SELECT avg(TIMESTAMPDIFF(MINUTE,j.notice_date,j.start_date))from oa_machine_record j WHERE j.mhid = a.mhid and j.scan_type='4' and j.start_date>=#{pd.lastStart} and j.start_date<=CONCAT(#{pd.lastEnd},' 23:59:59') GROUP BY j.mhid),0) as gggavgtz, ifnull((SELECT avg(TIMESTAMPDIFF(MINUTE,j.start_date,j.end_date))from oa_machine_record j WHERE j.mhid = a.mhid and j.scan_type='4' and j.start_date>=#{pd.lastStart} and j.start_date<=CONCAT(#{pd.lastEnd},' 23:59:59') GROUP BY j.mhid),0) as gggavgggg , ifnull((SELECT sum(TIMESTAMPDIFF(MINUTE,j.start_date,j.end_date))from oa_machine_record j WHERE j.mhid = a.mhid and j.scan_type='4' and j.start_date>=#{pd.lastStart} and j.start_date<=CONCAT(#{pd.lastEnd},' 23:59:59') GROUP BY j.mhid),0) as gggsum
from oa_machine a LEFT JOIN ( SELECT f.mhid,GROUP_CONCAT(f.cycleName,';',f.number) as cycleInfo from ( SELECT d.mhid,e.`name` as cycleName,count(1) as number from oa_machine_cycle d LEFT JOIN oa_company_basic e ON d.cycleid = e.ocbid GROUP BY d.mhid,e.`name` ) f GROUP BY mhid ) g ON a.mhid = g.mhid where a.company_id=#{pd.COMPANY_ID}
and a.type =#{pd.type}
and a.NAME LIKE CONCAT(CONCAT('%', #{pd.machineName}),'%')
ORDER by a.mhid
