mapper.select:
select a.NAME, a.NAME_EN, a.BIANMA, a.DEPARTMENT_ID, a.FUNCTIONS, a.TEL, a.EMAIL, a.SEX, a.BIRTHDAY, a.NATION, a.JOBTYPE, a.JOBJOINTIME, a.FADDRESS, a.POLITICAL, a.PJOINTIME, a.SFID, a.MARITAL, a.DJOINTIME, a.POST, a.POJOINTIME, a.EDUCATION, a.SCHOOL, a.MAJOR, a.FTITLE, a.CERTIFICATE, a.CONTRACTLENGTH, a.CSTARTTIME, a.CENDTIME, a.ADDRESS, a.USER_ID, a.BZ, a.STAFF_ID, a.COMPANY_ID, a.STATUS, a.PROVINCE, a.CITY, a.AREA, a.GRADUATE, b.NAME as departmentName, c.USERNAME, d.name as postName from a LEFT JOIN oa_department b on a.DEPARTMENT_ID = b.DEPARTMENT_ID LEFT JOIN SYS_USER c ON a.USER_ID = c.USER_ID LEFT JOIN oa_company_basic d ON d.ocbid = a.POST where a.deleted = 0 and a.COMPANY_ID =#{pd.COMPANY_ID}
and a.NAME LIKE CONCAT(CONCAT('%', #{pd.NAME}),'%')
and a.TEL LIKE CONCAT(CONCAT('%', #{pd.TEL}),'%')
and a.POST= #{pd.POST}
and a.DEPARTMENT_ID in ${pd.item}
and c.USERNAME LIKE CONCAT(CONCAT('%', #{pd.USERNAME}),'%')
and a.STATUS= #{pd.STATUS}
and a.DJOINTIME >= #{pd.DJOINTIMESTART}
and #{pd.DJOINTIMEEND} >=a.DJOINTIME
